package javaprogram;

public class SwaptheNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		int c=a+b;
		System.out.println("Before Swpping the a, b value+"+a+","+b);
		b=c-b;//30-20=10;
		a=c-b;//30-10=20;
		System.out.println("Afterswapping Swpping the a, b value+"+a+","+b);

	}

}
