package javaprogram;

public class SwaptheNumberswithout3rdvariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a= 10;
int b=20;
System.out.println("Before Swaping the value of a,b"+a+b);
a=a+b;// 10+20=30
b=a-b;//30-20=10
a=a-b;//30-10=20
System.out.println("Afterswapping the value of a,b"+a+b);
	}

}
