package javaprogram;

public class leftAngledTriangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int rows=5;
		for (int i = 1; i < rows; i++) {
			for (int j = 1; j <=rows-1; j++) {
				System.out.println();
				
			}
			//printing asterisus
			
		
		for (int k=1;k<=i;k++) {
			System.out.print("*");
		}

	}
		System.out.println();
	}

}
